'    l_region_rec                       apex_application_page_regions%rowtype;',
'    l_region_id                        varchar2(100);',
'',
'begin',
'',
'    --debug',
'    if apex_application.g_debug',
'    then',
'        apex_plugin_util.debug_dynamic_action',
'          ( p_plugin         => p_plugin',
'          , p_dynamic_action => p_dynamic_action',
'          );',
'    end if;',
'',
'    --get region infos about IG',
'    select aapr.* ',
'    into l_region_rec',
'    from apex_application_page_regions aapr',
'    where region_id = l_affected_region_id;',
'',
