'    ',
'    apex_json.free_output;',
'    ',
'    return l_result;',
'',
'exception when no_data_found then',
'',
'    apex_error.add_error ( p_message          => ''Please provide the designated Interactive Grid in the Affected Elements attribute!''',
'                         , p_display_location => apex_error.c_on_error_page );',
'    ',
'end render;'))
